import React from 'react';

import { Route, Switch, Redirect } from 'react-router-dom';

import { connect } from 'react-redux';

import { createStructuredSelector } from 'reselect';

import './App.css';

import HomePage from './pages/homepage/hompage.component';

import ShopPage from './pages/shop/shop.component.jsx';

import SignInAndSignUpPage from './pages/sign-in-and-sign-up/sign-in-and-sign-up.component';

import CheckoutPage from './pages/checkout/checkout.component';

import ContactFeedback from './pages/contact-feedback/contact-feedback.component';

import Header from './components/header/header.component.jsx';

import { auth, createUserProfileDocument, addCollectionAndDocuments } from './firebase/firebase.utils';

import { setCurrentUser } from './redux/user/user.actions';

import { selectCurrentUser } from './redux/user/user.selectors';

import { selectCollectionsForPreview } from './redux/shop/shop.selectors';

class App extends React.Component {
  unsubscribeFromAuth = null

  componentDidMount() {
    const { setCurrentUser, collectionAraay } = this.props; 



    this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
      if (userAuth) {
        const userRef = await createUserProfileDocument(userAuth);

        userRef.onSnapshot(snapShot => {
          this.props.setCurrentUser({
              id: snapShot.id,
              ...snapShot.data()
            })
        });
      }

      setCurrentUser(userAuth);
      addCollectionAndDocuments('collections', collectionAraay);
    });
  }

  componentWillUnmount() {
    this.unsubscribeFromAuth();
  }



  render() {
    return (
    <div>
      <Header/>
      <Switch>
         <Route exact path='/' component={HomePage} />
         <Route path='/shop' component={ShopPage} />
         <Route path='/contactfeedback' component={ContactFeedback} />
         <Route exact path='/checkout' component={CheckoutPage} />
         <Route 
         exact 
            path='/signin' 
              render={() => 
                  this.props.currentUser ? (
                   <Redirect to='/' />
                  ) : (
           <SignInAndSignUpPage />
         )
        } 
      />
    </Switch>
  </div>  
  );
}
}

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  collectionAraay: selectCollectionsForPreview
});

const mapDispatchToProps = dispatch => ({
  setCurrentUser: user => dispatch(setCurrentUser(user))
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
  )(App);
